import React from 'react';
import { Link } from 'react-router-dom';
import styled from '@emotion/styled';


function NavBar({logout, getOrders, user }){

    const Nav = styled.nav`
        position: absolute;
        top: 0px;
        left: 0px;
        width: 100%;
        background-color: #212a31;
        height: 100px;
        padding: 10px;
    `;

    const NavButton = styled.button`
        width: 120px;
        height: 60px;
        background-color: #212a31;
        color: white;
        font-family: "Roboto Condensed", sans-serif;
        font-size: 16px;
        outline: none;
        cursor: pointer;
        border: none;
        border-radius: 0.3rem;
        transition: all 0.5s;
        &:hover {
            background-color: #ffdb58;
            color: #212a31;
            transform: scale(1.1)
        }
        float: right;
        margin-right: 40px;
        margin-top: 10px;
        text-align: center;
    `;

    const logoStyle = {
        float: 'left', 
        marginLeft: '20px', 
        marginTop: '30px',
        color: 'white', 
        fontSize: '24px',
        textShadow: '1px 1px 1px #fff'
    };

    const greetingStyle = {
        color: 'white',
        fontSize: '16px',
        textShadow: 'none',
        marginTop: '15px'
    }

    const dropdownContentStyle = {
        marginTop: '0px' 
    }

    const showDropdown = () => {
        const dropdownContent = document.querySelector('.dropdown-content');
        dropdownContent.classList.toggle('on');
    }

    return (
        <Nav>
            <Link to='/home'>
            <div className="title" style={logoStyle}>Apple Shop <i className="fab fa-apple"></i>
            <div className="greeting" style={greetingStyle}>Welcome back, {user.username}</div>
            </div>
            </Link>
            <NavButton onClick={logout}>Logout</NavButton>
            <Link to='/orders'>
            <NavButton onClick={getOrders}>My Orders</NavButton>
            </Link>
            {user.role === 'administrator' && 
            <div className="dropdown">
                <NavButton onClick={showDropdown}>Manage Products<i className="fa fa-caret-down"></i></NavButton>
                <div className="dropdown-content">
                    <Link to='/create-product' onClick={showDropdown}>
                    <NavButton className="dropdown-item" style={dropdownContentStyle}>Create a Product</NavButton>
                    </Link>
                    <NavButton className="dropdown-item" style={dropdownContentStyle}>Update a Product</NavButton>
                    <NavButton className="dropdown-item" style={dropdownContentStyle}>Delete a Product</NavButton>
                </div>
            </div>
            }
            <Link to='/products'>
            <NavButton>Products</NavButton>
            </Link>
            <Link to='/password'>
            <NavButton>Manage Passwords</NavButton>
            </Link>
        </Nav>
    )
}

export default NavBar;
