import React, { useRef } from 'react';
import {FormDiv, Button, Input} from './styledElements';
import { toastConfig } from './styledElements'; 
import { toast, ToastContainer } from 'react-toastify';

function PasswordUpdate({user, updatePassword}) {

    const passwordRef = useRef();
    const newPasswordRef = useRef();
    const confirmPasswordRef = useRef();

    const handlePasswordUpdate = e => {
        e.preventDefault();
        if(confirmPasswordRef.current.value !== newPasswordRef.current.value) {
            return toast.error('Passwords do not match', toastConfig);
        }
        const params = {password: user.password, newPassword: newPasswordRef.current.value};
        updatePassword(e, params);

    }

    return (
        <FormDiv>
            <ToastContainer/>
            <form onSubmit={handlePasswordUpdate} className="password-update">
            <Input ref={passwordRef} type='password' name='currentPassword' id='currentPassword' placeholder='Enter current password' required/>
            <Input ref={newPasswordRef} type='password' name='newPassword' id='newPassword' placeholder='Enter new password' required/>
            <Input ref={confirmPasswordRef} type='password' name='confirmPassword' id='confirmPassword' placeholder='Reenter new password' required/>
            <Button type='submit' style={{width: '180px'}} >Update Password</Button>
            </form>
        </FormDiv>
    )
}

export default PasswordUpdate;
