import React, { useState } from 'react';
import styled from '@emotion/styled';
import ProductView from './ProductView';
import SearchBar from './SearchBar';
import { Input } from './styledElements';

function Catalogue({products}) {
    const Wrapper = styled.div`
        display: flex;
        flex-direction: column;
        position: relative;
        top: 120px;
        overflow: hidden;
        width: 100vw;
        margin-left: auto;
        margin-right: auto;
    `;

    const SearchDiv = styled.div`
        width: 50vw;
        height: 2rem;
        top: 150px;
        margin-right: auto;
        margin-left: auto;
        margin-top: 20px;
        margin-bottom: 20px;
    `;

    const [search, setSearch ] = useState('');

    const updateSearch = term => {
        setSearch(term);
    };

    const SearchBar = () => {
        return (
            <SearchDiv>
            <Input style={{width: '100%', border: 'solid 3px #212a31', borderRadius:'0.4rem'}} 
            type="text" 
            name="search" 
            id='search' 
            placeholder='Search'
            value={search}
            onChange={(e) => updateSearch(e.target.value)}
            />
            <i style={{float: 'right', position: 'relative', top: '-43px'}} className="fas fa-search"></i>
        </SearchDiv>
        )
    }
    return (
        <Wrapper>
            {SearchBar()}
            { search === '' &&
            products.map((product, i) => (
                <ProductView price={product.price} 
                title={product.name} 
                img_url={product.productImage && `http://localhost:5000/${product.productImage}`}
                id={product.id}
                videoURL={product.videoURL}
                key={i}
                />
            ))}
            { products.map((product, i) => 
            product.name.toLowerCase().includes(search.toLowerCase()) &&  (
                <ProductView price={product.price} 
                    title={product.name} 
                    img_url={product.productImage && `http://localhost:5000/${product.productImage}`}
                    id={product.id}
                    videoURL={product.videoURL}
                    key={i}
                />
            ))}
        </Wrapper>

    )
}

export default Catalogue;
