import React, { Fragment } from 'react';
import styled from '@emotion/styled';
import {Button} from './styledElements';
import OrderModal from './OrderModal';
import { usePersistedState } from '../utils';

function ProductView({img_url, price, title}) {
    const ProductDiv = styled.div`
        position: relative;
        z-index: 3;
        display: grid;
        grid-template-areas:
            'pic title'
            'pic price'
            'pic order';
        grid-template-columns: 2fr 1fr;
        grid-template-rows: 1fr 1fr;
        position: relative;
        margin-left: auto;
        margin-right: auto;
        margin-bottom: 20px;
        margin-top: 20px;
        padding: 40px;
        border: solid 5px #212a31;
        border-radius: 0.5rem;
        box-shadow: 2px 2px 2px 2px lightgray;
        width: 50vh;
        height: 100%;
    `;

    const ImageDiv = styled.div`
        grid-area: pic;
        width: 200px;
        height: 180px;
    `;
    const TitleDiv = styled.div`
        grid-area: title;
    `;
    const PriceDiv = styled.div`
        grid-area: price;
    `;


    const [showModal, setShowModal ] = usePersistedState("showModal", false);

    const toggleOrderModal = () => {
        const modal = document.querySelector('.modal');
        modal.classList.add('on');
    }


    return (
        <Fragment>

            <ProductDiv>
                <ImageDiv>
                    {img_url && <img src={img_url} alt={title} className="product-img"/>}
                </ImageDiv>
                <TitleDiv>
                    <p><strong>{title}</strong></p>
                </TitleDiv>
                <PriceDiv>
                    <strong>${new Intl.NumberFormat().format(price)}</strong>
                </PriceDiv>
                <Button style={{gridArea: 'order'}} onClick={toggleOrderModal}>Add to order</Button>
            </ProductDiv>
        </Fragment>
    )
}

export default ProductView;
