import React from 'react';
import ReactPlayer from 'react-player';

function ProductVideo ({videoURL, productId, showCloseButton, closeButton, setMountVideo, mountVideo}) {

    return (
        <div className={`video-div video-${productId}`} >
            <ReactPlayer 
                className="video-player"
                url={videoURL}
                playing={mountVideo}
                controls={false}
                width={'600px'}
                height={'500px'}
                pip={true}
                playsinline={true}
                stopOnUnmount={true}
            />
            { closeButton && <button 
                className="close-video" 
                onClick={() => { showCloseButton(); setMountVideo(!mountVideo)}}
                >
                Close <i className="far fa-times-circle"></i>
            </button>}
        </div>
    )
}

export default ProductVideo;
