import React, { Fragment, useState, useRef } from 'react';
import styled from '@emotion/styled';
import {Button, FormDiv, Input} from './styledElements';
import Modal from './Modal';
import ProductVideo from './ProductVideo';


function ProductView({img_url, price, title, id, videoURL, user, deleteProduct, available, updateProductAvailability, mobile, updateProduct}) {
    const ProductDiv = styled.div`
        position: relative;
        z-index: 3;
        display: grid;
        grid-template-areas:
            'pic title'
            'pic price'
            'pic order'
            'update video'
            'availability delete';
        @media (max-width: 768px){
            display: flex;
            flex-direction: column;
            align-items: center;
            width: 50vw;
        }
        grid-template-columns: 2fr 1fr;
        grid-template-rows: 1fr 1fr 1fr 1fr;
        position: relative;
        margin-left: auto;
        margin-right: auto;
        margin-bottom: 20px;
        margin-top: 20px;
        padding: 40px;
        border: none;
        border-radius: 0.5rem;

        width: 50vw;
        height: 100%;
        background: white;
        ;
    `;

    const ImageDiv = styled.div`
        grid-area: pic;
        width: 200px;
        height: 180px;
        transition: all 0.5s ease-in-out;
        &:hover {
            transform: scale(1.3);
        }
    `;
    const TitleDiv = styled.div`
        grid-area: title;
    `;
    const PriceDiv = styled.div`
        grid-area: price;
    `;

    const [ closeButton, setCloseButton ] = useState(false);
    const [ mountVideo, setMountVideo ] = useState(false);
    const [display, setDisplay ] = useState('');
    const [ updatedImage, setUpdatedImage ] = useState();

    const newPrice = useRef();
    const newTitle = useRef();

    const showCloseButton = () => {
        setTimeout(() => setCloseButton(!closeButton), 1500);
    }

    const toggle = thing => {
        thing = document.querySelector(`.${thing}-${id}`);
        thing.classList.toggle('on');
    }

    const handleAvailability = () => {
        const path = available ? `/products/${id}/unavailable` : `/products/${id}/available`;
        updateProductAvailability(path);
    }

    const handleUpdateProduct = e => {
        e.preventDefault();

        const data = new FormData();
        updatedImage && data.append('productImage', updatedImage, updatedImage.name);
        newTitle.current.value !== title && data.append('newName', newTitle.current.value);
        newPrice.current.value !== price && data.append('newPrice', newPrice.current.value);
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        };
        const params = {
            newName: newTitle.current.value,
            newPrice: newPrice.current.value
        }
        updatedImage ? updateProduct(e, id, data, config) : updateProduct(e, id, data);
    }

    const UpdateProductModal = () => {
        return (
            <FormDiv style={{top: '0'}}>
                <form className="update-product-form" onSubmit={handleUpdateProduct} encType="multipart/form-data">
                    <Input type='text' id='product-title' name='product-title' ref={newTitle} defaultValue={title}/>
                    <Input type='number' id='product-price' name='product-price' ref={newPrice} defaultValue={price}/>
                    <Input onChange={(e)=> setUpdatedImage(e.target.files[0])} type="file" name="productImage" id="productImage"/>
                    <Button style={{width: '160px'}} type="submit"> Confirm Update</Button>
                </form>
            </FormDiv>
        )
    }

    return (
        <Fragment>
            <Modal productId={id} toggle={toggle} display={display} deleteProduct={deleteProduct} UpdateProductModal={UpdateProductModal}/>
            { mountVideo && <ProductVideo 
                videoURL={videoURL} 
                toggle={toggle} 
                productId={id}
                showCloseButton={showCloseButton}
                closeButton={closeButton}
                mountVideo={mountVideo}
                setMountVideo={setMountVideo}
                mobile={mobile}
                />}
            <ProductDiv>
                <ImageDiv>
                    {img_url && <img src={img_url} alt={title} className="product-img" loading='lazy'/>}
                </ImageDiv>
                <TitleDiv>
                    <p><strong>Apple {title}</strong></p>
                </TitleDiv>
                <PriceDiv>
                    <strong>Price: ${new Intl.NumberFormat().format(price)}</strong>
                </PriceDiv>
                <Button className={available ? 'add-to-order-btn' : 'add-to-order-btn out-of-stock'} 
                style={{gridArea: 'order', width: mobile ? '130px' : '100px'}} 
                onClick={() => {toggle('modal'); setDisplay('order')}} 
                disabled={!available}>
                {available ? 'Add to order' : 'Out of Stock'}
                </Button>
                <Button style={{gridArea: 'video'}} 
                onClick={() => {showCloseButton(); setMountVideo(!mountVideo)}}> 
                Video <i className="fab fa-youtube"></i>
                </Button>
                {user.role === 'administrator' && 
                <Button 
                style={{gridArea: 'update', width: '150px'}} 
                onClick={() => {toggle('modal'); setDisplay('update')}}>
                Update Product</Button>}
                {user.role === 'administrator' && 
                <Button 
                style={{gridArea: 'delete'}} 
                onClick={() => {toggle('modal'); setDisplay('delete')}}>
                Delete</Button>}
                {user.role === 'administrator' && 
                <Button 
                style={{gridArea: 'availability', width: '150px'}} 
                onClick={() => handleAvailability()}>
                Update Availability</Button>}
            </ProductDiv>
        </Fragment>
    )
}

export default ProductView;
