import React, { useRef } from 'react';
import { FormDiv, Button, Input } from './styledElements';
import { shopApiInstance } from '../network';


function OrderModal({productId}) {

    const quantityRef = useRef();

    const closeModal = e => {
        e.preventDefault();
        const modal = document.querySelector(`.id-${productId}`);
        modal.classList.remove('on');
        console.log(productId, quantityRef.current.value);
        const params = {product: productId, quantity: quantityRef};
        shopApiInstance.post('/orders/', params)
        .then(response => {
            console.log(response.data);

        }).catch(err => {
            console.log(err.message);
        })
        e.target.reset();
    }

    return (
        <FormDiv className={`modal id-${productId}`}>
            <h3 className="order-modal">How many would you like to order?</h3>
            <form onSubmit={closeModal}>
            <Input type='number' name="quantity" id="quantity" defaultValue="1" ref={quantityRef}/>
            <br/>
            <Button type='submit'>Confirm Order</Button>
            </form>
        </FormDiv>
    )
}

export default OrderModal;
