import React from 'react';
import { Link } from 'react-router-dom';
import styled from '@emotion/styled';


function NavBar({logout, getOrders, user }){

    const Nav = styled.nav`
        position: absolute;
        top: 0px;
        left: 0px;
        width: 100%;
        background-color: #212a31;
        height: 100px;
        padding: 10px;
    `;

    const NavButton = styled.button`
        width: 120px;
        height: 60px;
        background-color: #212a31;
        color: white;
        font-family: "Roboto Condensed", sans-serif;
        font-size: 16px;
        outline: none;
        cursor: pointer;
        border: none;
        border-radius: 0.3rem;
        transition: all 0.5s;
        &:hover {
            background-color: #ffdb58;
            color: #212a31;
            transform: scale(1.1)
        }
        float: right;
        margin-right: 40px;
        margin-top: 10px;
        text-align: center;
    `;

    const logoStyle = {
        float: 'left', 
        marginLeft: '20px', 
        marginTop: '30px',
        color: 'white', 
        fontSize: '24px',
        textShadow: '1px 1px 1px #fff'
    };

    const greetingStyle = {
        color: 'white',
        fontSize: '16px',
        textShadow: 'none',
        marginTop: '15px'
    }

    return (
        <Nav>
            <Link to='/home'>
            <div className="title" style={logoStyle}>Apple Shop <i className="fab fa-apple"></i>
            <div className="greeting" style={greetingStyle}>Welcome back, {user.username}</div>
            </div>
            </Link>
            <NavButton onClick={logout}>Logout</NavButton>
            <Link to='/orders'>
            <NavButton onClick={getOrders}>My Orders</NavButton>
            </Link>
            {user.role === 'administrator' && 
                    <Link to='/create-product'>
                    <NavButton>Create a Product</NavButton>
                    </Link>
            }
            <Link to='/products'>
            <NavButton>Products</NavButton>
            </Link>
            <Link to='/password'>
            <NavButton>Manage Passwords</NavButton>
            </Link>
        </Nav>
    )
}

export default NavBar;
