import React from 'react';
import { Link } from 'react-router-dom';
import styled from '@emotion/styled';


function NavBar({logout, getOrders, user}){
    const Nav = styled.nav`
        position: absolute;
        top: 0px;
        left: 0px;
        width: 100%;
        background-color: #212a31;
        height: 100px;
        padding: 10px;
    `;

    const NavButton = styled.button`
        width: 120px;
        height: 60px;
        background-color: #212a31;
        color: white;
        font-family: "Roboto Condensed", sans-serif;
        font-size: 16px;
        outline: none;
        border: solid 2px white;
        border-radius: 0.3rem;
        &:hover {
            background-color: white;
            color: #212a31;
        }
        float: right;
        margin-right: 40px;
        margin-top: 10px;
    `;

    const logoStyle = {
        float: 'left', 
        marginLeft: '20px', 
        marginTop: '30px',
        color: 'white', 
        fontSize: '20px',
        textShadow: '1px 1px 1px #fff'
    };

    const greetingStyle = {
        left: '-100px',
        color: 'white'
    }

    return (
        <Nav>
            <Link to='/products'>
            <div className="title" style={logoStyle}>Apple Shop <i className="fab fa-apple"></i></div>
            </Link>
            <div className="greeting" style={greetingStyle}>Welcome back, {user}</div>

            <NavButton onClick={logout}>Logout</NavButton>
            <Link to='/orders'>
            <NavButton onClick={getOrders}>My Orders</NavButton>
            </Link>
        </Nav>
    )
}

export default NavBar;
