import React from 'react';
import styled from '@emotion/styled';
import { FormDiv, Button, Input } from './styledElements';


function OrderModal() {

    const closeModal = () => {
        const modal = document.querySelector('.modal');
        modal.classList.remove('on');
    }

    return (
        <FormDiv className='modal'>
            <h3 className="order-modal">How many would you like to order?</h3>
            <Input type='number' name="quantity" id="quantity" defaultValue="1"/>
            <Button onClick={closeModal}>Confirm Order</Button>
        </FormDiv>
    )
}

export default OrderModal;
