import React, { useRef, Fragment } from 'react';
import { Link } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {FormDiv, Button, Input} from './styledElements';
import { toastConfig } from './styledElements';
import styled from '@emotion/styled';

const LoginButton = styled.button`
        border: none;
        border-bottom: solid 1px black;
        outline: none;
        cursor: pointer;
        font-family: "Roboto Condensed", sans-serif;
        background: none;
        font-size: 16px;
        transition: all 0.3s;
        &:hover {
            background-color: #212a31;
            color: white;
            transform: scale(1.2);
            border: solid 1px white;
            border-radius: 0.4rem;
        }
    `;

function Register({handleRegister}) {


    const emailRef = useRef('');
    const passwordRef = useRef('');
    const confirmPasswordRef = useRef('');
    const usernameRef = useRef('');

    const validate = (e, params) => {
        if (passwordRef.current.value !== confirmPasswordRef.current.value) {
            toast.error('Passwords do not match.', toastConfig);
        } else {
            params = {email: emailRef.current.value, password: passwordRef.current.value, username: usernameRef.current.value};
            handleRegister(e, params);
        }
    }

    return (
        <Fragment>
            <ToastContainer/>
            <div className="header" style={{display: 'flex', justifyContent: 'center', fontSize: '24px'}}>
            <h1 >Welcome to Apple Shop <i className="fab fa-apple"></i></h1>
            </div>
            <FormDiv style={{top: '60px'}}>
                <form onSubmit={validate}>
                <Input type="text" 
                name="username" 
                id="username" 
                placeholder="Username" 
                ref={usernameRef}/>
                <br/>
                <Input type="email" 
                name="email" 
                id="email" 
                placeholder="Email address" 
                ref={emailRef}/>
                <br/>
                <Input type="password" 
                name="password" 
                id="password" 
                placeholder="Password"
                ref={passwordRef}/>
                <br/>
                <Input type="password" 
                name="confirm" 
                id="confirm" 
                placeholder="Confirm Password"
                ref={confirmPasswordRef}/>
                <br/>
                <Button type="submit">Register</Button>
                </form>
                <footer><Link to='/'><LoginButton>Log in instead?</LoginButton></Link></footer>
            </FormDiv>
        </Fragment>
    )

}

export default Register;
