import React, { Fragment, useState } from 'react';
import styled from '@emotion/styled';
import {Button} from './styledElements';
import OrderModal from './OrderModal';
import ProductVideo from './ProductVideo';

function ProductView({img_url, price, title, id, videoURL, user, deleteProduct}) {
    const ProductDiv = styled.div`
        position: relative;
        z-index: 3;
        display: grid;
        grid-template-areas:
            'pic title'
            'pic price'
            'pic order'
            'pic video'
            'update delete';
        grid-template-columns: 2fr 1fr;
        grid-template-rows: 1fr 1fr 1fr 1fr;
        position: relative;
        margin-left: auto;
        margin-right: auto;
        margin-bottom: 20px;
        margin-top: 20px;
        padding: 40px;
        border: solid 5px #212a31;
        border-radius: 0.5rem;
        box-shadow: 2px 2px 2px 2px lightgray;
        width: 50vh;
        height: 100%;
        background: white;
    `;

    const ImageDiv = styled.div`
        grid-area: pic;
        width: 200px;
        height: 180px;
    `;
    const TitleDiv = styled.div`
        grid-area: title;
    `;
    const PriceDiv = styled.div`
        grid-area: price;
    `;

    const [ closeButton, setCloseButton ] = useState(false);
    const [ mountVideo, setMountVideo ] = useState(false);

    const showCloseButton = () => {
        setTimeout(() => setCloseButton(!closeButton), 1500);
    }

    const toggle = thing => {
        thing = document.querySelector(`.${thing}-${id}`);
        thing.classList.toggle('on');
    }

    return (
        <Fragment>
            <OrderModal productId={id} toggle={toggle}/>
            { mountVideo && <ProductVideo 
                videoURL={videoURL} 
                toggle={toggle} 
                productId={id}
                showCloseButton={showCloseButton}
                closeButton={closeButton}
                mountVideo={mountVideo}
                setMountVideo={setMountVideo}
                />}
            <ProductDiv>
                <ImageDiv>
                    {img_url && <img src={img_url} alt={title} className="product-img"/>}
                </ImageDiv>
                <TitleDiv>
                    <p><strong>Apple {title}</strong></p>
                </TitleDiv>
                <PriceDiv>
                    <strong>Price: ${new Intl.NumberFormat().format(price)}</strong>
                </PriceDiv>
                <Button style={{gridArea: 'order'}} onClick={() => toggle('modal')}>Add to order</Button>
                <Button style={{gridArea: 'video'}} onClick={() => {showCloseButton(); setMountVideo(!mountVideo)}}> Video <i className="fab fa-youtube"></i></Button>
                {user.role === 'administrator' && <Button style={{gridArea: 'delete'}} onClick={() => deleteProduct(id)}>Delete</Button>}
                {user.role === 'administrator' && <Button style={{gridArea: 'update'}}>Update</Button>}
            </ProductDiv>
        </Fragment>
    )
}

export default ProductView;
