import React, { Fragment, useState ,useRef } from 'react';
import {shopApiInstance} from '../network';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Login from './Login';
//import { Toast } from 'react-toastify/dist/components';

function App() {


    const [ token, setToken ] = useState('');

    const toastConfig = {
        position: "top-center",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
    }

    const handleLogin = (e, params) => {
        e.preventDefault();
        //const params = {email: emailRef.current.value, password: passwordRef.current.value};
        shopApiInstance.post('/users/signin', params)
        .then(response => {
            e.target.reset();
            console.log(response.data);
            setToken(response.data.token);
            toast.success("Successfully logged in!", toastConfig);
        })
        .catch(err => {
            console.log(err);
            toast.error(err.message, toastConfig);
        });
    }

    return (
        <Fragment>
            <ToastContainer />
            <Login handleLogin={handleLogin}/>
        </Fragment>


    )
};

export default App;
