import React, { Fragment } from 'react';
import styled from '@emotion/styled';

function OrderView ({title, img_url, price, total, quantity}) {
    const OrderDiv = styled.div`
        position: relative;
        z-index: 3;
        display: grid;
        grid-template-areas:
            'pic title'
            'pic price'
            'pic quantity'
            'total total';
        grid-template-columns: 2fr 1fr;
        grid-template-rows: 1fr 1fr 1fr;
        position: relative;
        margin-left: auto;
        margin-right: auto;
        margin-bottom: 20px;
        margin-top: 20px;
        padding: 40px;
        border: solid 5px #212a31;
        border-radius: 0.5rem;
        box-shadow: 2px 2px 2px 2px lightgray;
        width: 50vh;
        height: 100%;
    `;

    const ImageDiv = styled.div`
        grid-area: pic;
        width: 200px;
        height: 180px;
    `;
    const TitleDiv = styled.div`
        grid-area: title;
    `;
    const PriceDiv = styled.div`
        grid-area: price;
    `;

    const QuantityDiv = styled.div`
        grid-area: quantity;
    `;

    const TotalDiv = styled.div`
        justify-self: end;
        grid-area: total;
        margin-right: 40px;
    `;

    return (
        <Fragment>
            <OrderDiv>
                <ImageDiv>
                    <img src={img_url} alt={title} className="product-img"/>
                </ImageDiv>
                <TitleDiv>
                    <strong>{title}</strong>
                </TitleDiv>
                <QuantityDiv>
                    <strong>Quantity: {quantity}</strong>
                </QuantityDiv>
                <PriceDiv>
                    <strong>Unit Price: {price}</strong>
                </PriceDiv>
                <TotalDiv>
                    <strong>TOTAL: {total}</strong>
                </TotalDiv>
            </OrderDiv>
        </Fragment>
    )
}

export default OrderView;
