import React, { Fragment, useState } from 'react';
import styled from '@emotion/styled';
import {Button, FormDiv, Input} from './styledElements';
import OrderModal from './OrderModal';
import ProductVideo from './ProductVideo';
import { Modal, ModalBody } from 'reactstrap';

function ProductView({img_url, price, title, id, videoURL, user, deleteProduct, available, updateProductAvailability, mobile}) {
    const ProductDiv = styled.div`
        position: relative;
        z-index: 3;
        display: grid;
        grid-template-areas:
            'pic title'
            'pic price'
            'pic order'
            'update video'
            'availability delete';
        @media (max-width: 768px){
            display: flex;
            flex-direction: column;
            align-items: center;
            width: 50vw;
        }
        grid-template-columns: 2fr 1fr;
        grid-template-rows: 1fr 1fr 1fr 1fr;
        position: relative;
        margin-left: auto;
        margin-right: auto;
        margin-bottom: 20px;
        margin-top: 20px;
        padding: 40px;
        border: none;
        border-radius: 0.5rem;

        width: 50vw;
        height: 100%;
        background: white;
        ;
    `;

    const ImageDiv = styled.div`
        grid-area: pic;
        width: 200px;
        height: 180px;
        transition: all 0.5s ease-in-out;
        &:hover {
            transform: scale(1.3);
        }
    `;
    const TitleDiv = styled.div`
        grid-area: title;
    `;
    const PriceDiv = styled.div`
        grid-area: price;
    `;

    const [ closeButton, setCloseButton ] = useState(false);
    const [ mountVideo, setMountVideo ] = useState(false);
    const [display, setDisplay ] = useState('');
    const [ updateModal, setUpdateModal ] = useState(false);
    const [ updatedImage, setUpdatedImage ] = useState();

    const showCloseButton = () => {
        setTimeout(() => setCloseButton(!closeButton), 1500);
    }

    const toggle = thing => {
        thing = document.querySelector(`.${thing}-${id}`);
        thing.classList.toggle('on');
    }

    const handleAvailability = () => {
        const path = available ? `/products/${id}/unavailable` : `/products/${id}/available`;
        updateProductAvailability(path);
    }

    return (
        <Fragment>
            <Modal 
            isOpen={updateModal} 
            className='udpate-modal' 
            size='md' 
            scrollable={true}
            centered={true}>
                <ModalBody>
                    <FormDiv>
                        <form className="update-product-form">
                            <Input type='text' id='product-title' name='product-title' value={title}/>
                            <Input type='number' id='product-price' name='product-price' value={price}/>
                            <Input onChange={(e)=> setUpdatedImage(e.target.files[0])} type="file" name="productImage" id="productImage" placeholder="Upload a product new product image"/>
                            <Button style={{width: '260px'}} type="submit">Create Product</Button>
                        </form>
                        <Button style={{width: '260px'}} type="button" onClick={() => setUpdateModal(false)}>Cancel</Button>
                    </FormDiv>
                </ModalBody>
            </Modal>
            <OrderModal productId={id} toggle={toggle} display={display} deleteProduct={deleteProduct}/>
            { mountVideo && <ProductVideo 
                videoURL={videoURL} 
                toggle={toggle} 
                productId={id}
                showCloseButton={showCloseButton}
                closeButton={closeButton}
                mountVideo={mountVideo}
                setMountVideo={setMountVideo}
                mobile={mobile}
                />}
            <ProductDiv>
                <ImageDiv>
                    {img_url && <img src={img_url} alt={title} className="product-img" loading='lazy'/>}
                </ImageDiv>
                <TitleDiv>
                    <p><strong>Apple {title}</strong></p>
                </TitleDiv>
                <PriceDiv>
                    <strong>Price: ${new Intl.NumberFormat().format(price)}</strong>
                </PriceDiv>
                <Button className={available ? 'add-to-order-btn' : 'add-to-order-btn out-of-stock'} 
                style={{gridArea: 'order', width: mobile ? '130px' : '100px'}} 
                onClick={() => {toggle('modal'); setDisplay('order')}} 
                disabled={!available}>
                {available ? 'Add to order' : 'Out of Stock'}
                </Button>
                <Button style={{gridArea: 'video'}} 
                onClick={() => {showCloseButton(); setMountVideo(!mountVideo)}}> 
                Video <i className="fab fa-youtube"></i>
                </Button>
                {user.role === 'administrator' && 
                <Button 
                style={{gridArea: 'update', width: '150px'}} 
                onClick={() => setUpdateModal(true)}>
                Update Product</Button>}
                {user.role === 'administrator' && 
                <Button 
                style={{gridArea: 'delete'}} 
                onClick={() => {toggle('modal'); setDisplay('delete')}}>
                Delete</Button>}
                {user.role === 'administrator' && 
                <Button 
                style={{gridArea: 'availability', width: '150px'}} 
                onClick={() => handleAvailability()}>
                Update Availability</Button>}
            </ProductDiv>
        </Fragment>
    )
}

export default ProductView;
