import React from 'react';
import ReactPlayer from 'react-player';

function ProductVideo ({videoURL, play, toggle, productId, setPlay}) {

    return (
        <div className={`video-div video-${productId}`} style={{top: window.scrollY > 0 ? '-120px' : '0px'}}>
            <ReactPlayer 
                className="video-player"
                url={videoURL}
                playing={play}
                controls={true}
                width={'600px'}
                height={'500px'}
                pip={true}
                playsinline={true}
                stopOnUnmount={true}
            />
            { closeButton && <button 
                className="close-video" 
                onClick={() => {toggle('video'); setPlay(!play);}}
                >
                Close<i className="far fa-times-circle"></i>
            </button>}
        </div>
    )
}

export default ProductVideo;
