import React from 'react';
import styled from '@emotion/styled';
import {Button} from './styledElements';
import { Link } from 'react-router-dom';

function Home () {

    const StyledWrapper = styled.div`
        width: 50vw;
        position: relative;
        top: 200px;
        background-color: white;
        border: solid 5px #212a31;
        border-radius: 0.5rem;
        box-shadow: 2px 2px 2px 2px lightgray;
        height: 100%;
        display: flex;
        flex-direction: column;
        text-align: center;
        color: #212a31;
        margin-left: auto;
        margin-right: auto;
        padding: 40px;
    `;

    return (
        <StyledWrapper>
            <h1 className="welcome">Welcome to the Apple Shop <i className="fab fa-apple"></i>!</h1>
            <h2 className="landing">Your one stop shopping experience for all things Apple.We pride ourselves in being a premier supplier of Apple products.</h2>
            <h3 className="go-browse">Browse our complete catalogue.</h3>
            <Link to='/products'><Button>Browse</Button></Link>
        </StyledWrapper>
    )
}

export default Home;
