import axios from 'axios';

const shopApiInstance = axios.create({
    baseURL: window.env.SHOP_REST_API_BASE_URL

});

const tokenCheckInterceptor = shopApiInstance.interceptors.response.use(
    response => {
    console.log(response.headers['Shop-Refreshed-Access-Token'])
    return response;
  }, 
  error => {
    return Promise.reject(error);
  });

const setBearerToken = token => {
    shopApiInstance.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    console.log(`Set bearer token, ${token}`);
}

const isUserExpired = exp => {
    if (exp * 1000 < Date.now()) {
        console.warn('token expired')
        tokenCheckInterceptor();
    }
}

export {shopApiInstance, setBearerToken, isUserExpired};
