import React, { useRef } from 'react';
import { FormDiv, Button, Input } from './styledElements';

function CreateProduct ({addProduct}) {

    const nameRef = useRef();
    const priceRef = useRef();
    const imageRef = useRef();
    const videoRef = useRef();

    const handleCreateProduct = e => {
        const params = { 
            name: nameRef.current.value,
            price: priceRef.current.value,
            productImage: imageRef.current.value,
            videURL: videoRef.current.value,
        }
        e.preventDefault();
        shopApiInstance.post('/products', params)
        .then( response => {
            console.log(response.data);
            toast.success('Product successfully created', toastConfig);
            history.push('/products');
        }).catch(err => {
            toast.error(err.message, toastConfig);
        })
        e.target.reset();
    }

    return (
        <FormDiv enctype="multipart/form-data" onSubmit={handleCreateProduct}>
            <Input ref={nameRef} type="text" name="productName" id="productName" placeholder="Enter a product name" required/>
            <Input ref={priceRef} type="number" name="price" id="price" placeholder="Set a product price" required/>
            <Input ref={imageRef} type="file" name="productImage" id="productImage" placeholder="Upload a product image" required/>
            <Input ref={videoRef} type="text" name="videoURL" id="videoURL" placeholder="Paste a video URL for the product ad" required/>
            <Button style={{width: '260px'}} type="submit">Create Product</Button>
        </FormDiv>
    )
}

export default CreateProduct;
