import React, { useRef, Fragment } from 'react';
import 'react-toastify/dist/ReactToastify.css';
import styled from '@emotion/styled';

function Login({handleLogin}) {
    const FormDiv = styled.div`
        top: 200px;
        margin-left: auto;
        margin-right: auto;
        position: relative;
        display: flex;
        flex-direction: column;
        width: 300px;
        align-items: center;
        border: solid 2px black;
        border-radius: 0.3rem;
        padding: 20px;
    `;

    const Input = styled.input`
        height: 1.5em;
        width: 150px;
        border-bottom: 2px solid gray;
        margin-top: 10px;
        margin-bottom: 10px;
        padding: 5px;
        font-family: "Roboto Condensed", sans-serif;
        font-size: 16px;
        border-top: none;
        border-left: none;
        border-right: none;
    `;

    const Button = styled.button`
        height: 2.5em;
        font-size: 16px;
        width: 100px;
        padding: 5px;
        margin-top: 10px;
        color: white;
        background-color: #36454f;
        &:hover {
            background-color: #171d22;
            transform: scale(1.1);
        }
        outline: none;
        cursor: pointer;
        &:active {
            transform: translateY(4px);
        }
        border-radius: 0.4rem;
        font-family: "Roboto Condensed", sans-serif;
        font-weight: 700;
        border: none;
    `;

    const emailRef = useRef();
    const passwordRef = useRef();

    const handleSubmit = e => {
        const params = {email: emailRef.current.value, password: passwordRef.current.value};
        handleLogin(e, params);
    }

    return (
        <Fragment>
            <FormDiv>
                <form onSubmit={handleSubmit}>
                <Input type="email" 
                name="email" 
                id="email" 
                placeholder="Email address" 
                ref={emailRef}/>
                <Input type="password" 
                name="password" 
                id="password" 
                placeholder="Password"
                ref={passwordRef}/><br></br>
                <Button type="submit">Login</Button>
                </form>
                <footer>New user? <button>Register here</button></footer>
            </FormDiv>
        </Fragment>
    )
}

export default Login;
