import React, { useRef, Fragment } from 'react';
import { Button, Input } from './styledElements';
import { shopApiInstance } from '../network';
import { toast, ToastContainer } from 'react-toastify';
import { toastConfig } from './styledElements'; 


function OrderModal({productId, toggle}) {

    const quantityRef = useRef();

    const placeOrder = e => {
        e.preventDefault();
        toggle('modal');
        console.log(productId, quantityRef.current.value);
        const params = {product: productId, quantity: quantityRef.current.value};
        shopApiInstance.post('/orders/', params)
        .then(response => {
            console.log(response.data);
            toast.dark(response.data.message, toastConfig);
        }).catch(err => {
            console.log(err.message);
            toast.error(err.message, toastConfig);
        })
        e.target.reset();
    }

    return (
        <Fragment>
            <ToastContainer />
            <div className={`modal modal-${productId}`} style={{top: window.scrollY > 0 ? '-120px' : '0px'}}>
                <div className="modal-content">
                <h3 className="order-modal">How many would you like to order?</h3>
                <form onSubmit={placeOrder}>
                <Input 
                type='number' 
                name="quantity" 
                id="quantity" d
                efaultValue="1" 
                ref={quantityRef} 
                style={{textAlign: 'center'}}/>
                <br/>
                <Button type='submit'>Confirm Order</Button>
                </form>
                <Button style={{alignSelf: 'center'}} onClick={() => toggle('modal')}>Cancel</Button>
                </div>
        </div>
    </Fragment>
    )
}

export default OrderModal;
