import React, { useRef, Fragment, useState } from 'react';
import { Link } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {FormDiv, Button, Input} from './styledElements';
import { toastConfig } from './styledElements';
import styled from '@emotion/styled';
import { CheckSquare, XCircle } from 'react-feather';

const LoginButton = styled.button`
        border: none;
        border-bottom: solid 1px black;
        outline: none;
        cursor: pointer;
        font-family: "Roboto Condensed", sans-serif;
        background: none;
        font-size: 16px;
        transition: all 0.3s;
        &:hover {
            background-color: #212a31;
            color: white;
            transform: scale(1.2);
            border: solid 1px white;
            border-radius: 0.4rem;
        }
    `;

    const PasswordChecks = styled.div`
        width: 250px;
        height: 170px;
        display: flex;
        flex-direction: column;
        row-gap: 0.7rem;
        padding: 10px;
        margin-top: 5px;
        margin-bottom: 5px;
        background: linear-gradient(0.25turn, #cbd0d8, #f4f3ea);
        border: none;
        border-radius: 4px;
    `;

    const Check = styled.div`
        height: 1.5rem;
        width: fit-content;
        padding: 3px;
        display: flex;
        flex-direction: row;
    `;

function Register({handleRegister}) {


    const [password, setPassword ] = useState('');

    const emailRef = useRef('');
    const passwordRef = useRef('');
    const confirmPasswordRef = useRef('');
    const usernameRef = useRef('');

    const validate = (e, params) => {
        if (passwordRef.current.value !== confirmPasswordRef.current.value) {
            toast.error('Passwords do not match.', toastConfig);
        } else {
            params = {email: emailRef.current.value, password: passwordRef.current.value, username: usernameRef.current.value};
            handleRegister(e, params);
        }
    }

    const characterCountRegex = /.{8,}/;

    return (
        <Fragment>
            <ToastContainer/>
            <div className="header" style={{display: 'flex', justifyContent: 'center', fontSize: '24px'}}>
            <h1 >Welcome to Apple Shop <i className="fab fa-apple"></i></h1>
            </div>
            <FormDiv style={{top: '60px'}}>
                <form onSubmit={validate}>
                <Input type="text" 
                name="username" 
                id="username" 
                placeholder="Username" 
                ref={usernameRef}/>
                <br/>
                <Input type="email" 
                name="email" 
                id="email" 
                placeholder="Email address" 
                ref={emailRef}/>
                <br/>
                <Input type="password" 
                name="password" 
                id="password" 
                placeholder="Password"
                //ref={passwordRef}
                onChange={(e) => setPassword(e.target.value)}
                value={password}/>
                <br/>
                <PasswordChecks>
                    <Check>
                        {characterCountRegex.test(passwordRef.current.value) ? <CheckSquare size={20} style={{marginRight: '0.5rem', color: 'green'}}/>
                         : <XCircle size={20} style={{marginRight: '0.5rem', color: 'red'}}/>}
                        <span style={{fontSize: '14px'}}>Password must contain atleast 8 characters</span>
                    </Check>
                    <Check>
                        <CheckSquare size={20} style={{marginRight: '0.5rem', color: 'green'}}/>
                        <span style={{fontSize: '14px'}}>Password must contain atleast one uppercase</span>
                    </Check>
                    <Check>
                        <CheckSquare size={20} style={{marginRight: '0.5rem', color: 'green'}}/>
                        <span style={{fontSize: '14px'}}>Password must contain atleast one lowercase</span>
                    </Check>
                    <Check>
                        <CheckSquare size={20} style={{marginRight: '0.5rem', color: 'green'}}/>
                        <span style={{fontSize: '14px'}}>Password must contain atleast one special character</span>
                    </Check>
                </PasswordChecks>
                <Input type="password" 
                name="confirm" 
                id="confirm" 
                placeholder="Confirm Password"
                ref={confirmPasswordRef}/>
                <br/>
                <Button type="submit">Register</Button>
                </form>
                <footer><Link to='/'><LoginButton>Log in instead?</LoginButton></Link></footer>
            </FormDiv>
        </Fragment>
    )

}

export default Register;
