import axios from 'axios';
import jwt_decode from 'jwt-decode';

const shopApiInstance = axios.create({
    baseURL: window.env.SHOP_REST_API_BASE_URL

});

shopApiInstance.interceptors.response.use(
    response => {
    let newAccessToken;
    //const currentDecodedToken = jwt_decode(shopApiInstance.defaults.headers.common['Authorization'].split(' '[1]));
    //console.info(currentDecodedToken)
    // if (response.headers['shop-refreshed-access-token']){
    //     newAccessToken = response.headers['shop-refreshed-access-token']
    //     setBearerToken(newAccessToken);
    //     console.info('renewed access token')
    // }
    console.log(shopApiInstance.defaults.headers.common['Authorization'])
    return response;
  }, 
  error => {
    return Promise.reject(error);
  });

const setBearerToken = token => {
    shopApiInstance.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    console.log(`Set bearer token, ${token}`);
}

const isUserExpired = exp => {
    if (exp * 1000 < Date.now()) {
        console.warn('token expired')
        //tokenCheckInterceptor();
    }
}

export {shopApiInstance, setBearerToken, isUserExpired};
