import React, { useRef } from 'react';
import { FormDiv, Button, Input } from './styledElements';


function OrderModal({productId}) {

    const quantityRef = useRef();

    const closeModal = () => {
        const modal = document.querySelector(`${productId}`);
        modal.classList.remove('on');
        console.log(productId, quantityRef.current.value);
    }

    return (
        <FormDiv className={`modal ${productId}`}>
            <h3 className="order-modal">How many would you like to order?</h3>
            <Input type='number' name="quantity" id="quantity" defaultValue="1" ref={quantityRef}/>
            <Button onClick={closeModal}>Confirm Order</Button>
        </FormDiv>
    )
}

export default OrderModal;
