import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from '@emotion/styled';


function NavBar({logout, getOrders, user}){
    const Nav = styled.nav`
        position: absolute;
        top: 0px;
        left: 0px;
        width: 100%;
        background-color: #212a31;
        height: 100px;
        padding: 10px;
    `;

    const NavButton = styled.button`
        width: 120px;
        height: 60px;
        background-color: #212a31;
        color: white;
        font-family: "Roboto Condensed", sans-serif;
        font-size: 16px;
        outline: none;
        cursor: pointer;
        border: solid 2px white;
        border-radius: 0.3rem;
        &:hover {
            background-color: white;
            color: #212a31;
        }
        float: right;
        margin-right: 40px;
        margin-top: 10px;
    `;

    const logoStyle = {
        float: 'left', 
        marginLeft: '20px', 
        marginTop: '30px',
        color: 'white', 
        fontSize: '24px',
        textShadow: '1px 1px 1px #fff'
    };

    const greetingStyle = {
        color: 'white',
        fontSize: '16px',
        textShadow: 'none',
        marginTop: '15px'
    }

    const dropdownContentStyle = {
        display: showDropdown ? 'flex' : 'none', 
        flexDirection: 'column', 
        position: 'absolute', 
        left: '678px', 
        top: '100px', 
        zIndex: '100'
    }

    const [ showDropdown, setShowDropdown ] = useState(false);

    return (
        <Nav>
            <Link to='/products'>
            <div className="title" style={logoStyle}>Apple Shop <i className="fab fa-apple"></i>
            <div className="greeting" style={greetingStyle}>Welcome back, {user.username}</div>
            </div>
            </Link>
            <NavButton onClick={logout}>Logout</NavButton>
            <Link to='/orders'>
            <NavButton onClick={getOrders}>My Orders</NavButton>
            </Link>
            {user.role === 'administrator' && 
            <div className="dropdown">
                <NavButton onClick={setShowDropdown(!showDropdown)}>Manage Products <i className="fa fa-caret-down"></i></NavButton>
                <div className="dropdown-content" style={dropdownContentStyle}>
                    <NavButton>Create a Product</NavButton>
                    <NavButton>Update a Product</NavButton>
                    <NavButton>Delete a Product</NavButton>
                </div>
            </div>
            }
        </Nav>
    )
}

export default NavBar;
