import React, { Fragment, useState ,useRef } from 'react';
import {shopApiInstance} from './network';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import styled from '@emotion/styled'

function App() {

    const FormDiv = styled.div`
        top: 200px;
        margin-left: auto;
        margin-right: auto;
        position: relative;
        display: flex;
        flex-direction: column;
        width: 300px;
        align-items: center;
        justify-content: center;
        border: solid 2px black;
        border-radius: 0.3rem;
        padding: 20px;
    `;

    const Input = styled.input`
        height: 1.5em;
        width: 150px;
        border-bottom: 2px solid gray;
        margin-top: 10px;
        margin-bottom: 10px;
        padding: 5px;
        font-family: "Roboto Condensed", sans-serif;
        font-size: 16px;
        border-top: none;
        border-left: none;
        border-right: none;
    `;

    const Button = styled.button`
        height: 2.5em;
        font-size: 16px;
        width: 100px;
        padding: 5px;
        margin-top: 10px;
        color: white;
        background-color: #36454f;
        &:hover {
            background-color: #171d22;
            transform: scale(1.1);
        }
        outline: none;
        cursor: pointer;
        &:active {
            transform: translateY(4px);
        }
        border-radius: 0.4rem;
        font-family: "Roboto Condensed", sans-serif;
        font-weight: 700;
        border: none;
    `;

    const [ token, setToken ] = useState('');

    const emailRef = useRef();
    const passwordRef = useRef();

    const toastConfig = {
        position: "top-center",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
    }

    const handleLogin = e => {
        e.preventDefault();
        const params = {email: emailRef.current.value, password: passwordRef.current.value};
        shopApiInstance.post('/users/signin', params)
        .then(response => {
            e.target.reset();
            console.log(response.data);
            setToken(response.data.token);
            toast.success("Successfully logged in!", toastConfig);
        })
        .catch(err => {
            console.log(err);
            toast.error(err.message, toastConfig);
        });
    }

    return (
        <Fragment>
            <ToastContainer/>
            <FormDiv>
                <form onSubmit={handleLogin}>
                <Input type="email" 
                name="email" 
                id="email" 
                placeholder="Email address" 
                ref={emailRef}/>
                <Input type="password" 
                name="password" 
                id="password" 
                placeholder="Password"
                ref={passwordRef}/><br></br>
                <Button type="submit">Login</Button>
                </form>
            </FormDiv>
        </Fragment>
    )
};

export default App;
