import React from 'react';
import ReactPlayer from 'react-player';

function ProductVideo ({videoURL, play, toggleVideo, productId}) {

    return (
        <div className={`video-div video-${productId}`} style={{top: window.scrollY > 0 ? '-120px' : '0px'}}>
            <ReactPlayer 
                className="video-player"
                url={videoURL}
                playing={play}
                controls={true}
                width={'600px'}
                height={'500px'}
                stopOnUnmount={true}
            />
            <button className="close-video" onClick={toggleVideo}><i className="far fa-times-circle"></i></button>
        </div>
    )
}

export default ProductVideo;
